-- VIEW: MAXIMO.AHLOCRRPLAN
CREATE OR REPLACE VIEW "MAXIMO"."AHLOCRRPLAN" AS
select locations.location, ahrrplan.description, locoper.failurecode, locoper.locpriority, locations.classstructureid, locations.siteid, locations.orgid, locations.type,	locations.status, locations.health, locations.expectedlife, locations.estendoflife, locations.expectedlifedate, ahrrplan.targetdate, ahrrplan.actualdate, ahrrplan.estcost, ahrrplan.actualcost, ahrrplan.estlaborhours, ahrrplan.actlaborhours, ahrrplan.estdowntime, ahrrplan.actdowntime, ahrrplan.rrplan, locations.installdate,ahrrplan.objectname, 	ahrrplan.estopex, ahrrplan.estcapex, ahrrplan.actopex,ahrrplan.actcapex from locations, locoper, ahrrplan where locations.location = locoper.location and locations.siteid = locoper.siteid and locations.location =ahrrplan.location and locations.siteid = ahrrplan.siteid and ahrrplan.objectname = 'LOCATIONS'
