-- VIEW: MAXIMO.TOOLITEM
CREATE OR REPLACE VIEW "MAXIMO"."TOOLITEM" AS
select item.attachonissue, item.capitalized, item.classstructureid, item.commodity, item.commoditygroup, item.conditionenabled, item.description, item.externalrefid, item.groupname, item.hardresissue, item.hasld, item.in19, item.in20, item.in21, item.in22, item.in23, item.in24, item.in25, item.in26, item.in27, item.inspectionrequired, item.iscrew, item.iskit, item.issueunit, item.itemid, item.itemnum, item.itemsetid, item.itemtype, item.langcode, item.lottype, item.maxissue, item.metername, item.msdsnum, item.orderunit, item.outside, item.ownersysid, item.pluscisinhousecal, item.pluscismte, item.pluscismteclass, item.pluscsolution, item.prorate, item.receipttolerance, item.rotating, item.rowstamp, item.sendersysid, item.sourcesysid, item.sparepartautoadd, item.status, item.statusdate, item.taxexempt from item where itemtype  in (select value from synonymdomain where domainid = 'ITEMTYPE'  and maxvalue = 'TOOL')
