-- VIEW: MAXIMO.LEASEVIEWLINE
CREATE OR REPLACE VIEW "MAXIMO"."LEASEVIEWLINE" ("CATALOGCODE", "CHGPRICEONUSE", "CHGQTYONUSE", "COMMODITY", "COMMODITYGROUP", "CONDITIONCODE", "CONTRACTLINEID", "CONTRACTLINENUM", "CONTRACTNUM", "CONTRACTTYPE", "DESCRIPTION", "ENTERBY", "ENTERDATE", "HASLD", "HASPAYMENTSCHED", "INSPECTIONREQUIRED", "ITEMNUM", "ITEMSETID", "LANGCODE", "LEADTIME", "LEASEENDVALUE", "LINECOST", "LINECOST2", "LINESTATUS", "LINETYPE", "MANUFACTURER", "MODELNUM", "ORDERQTY", "ORDERUNIT", "ORGID", "POREQUIRED", "REMARK", "REVISIONNUM", "REVSTATUS", "ROWSTAMP", "UNITCOST") AS SELECT contractline.catalogcode, contractline.chgpriceonuse, contractline.chgqtyonuse, contractline.commodity, contractline.commoditygroup, contractline.conditioncode, contractline.contractlineid, contractline.contractlinenum, contractline.contractnum, contractline.contracttype, contractline.description, contractline.enterby, contractline.enterdate, contractline.hasld, contractline.haspaymentsched, contractline.inspectionrequired, contractline.itemnum, contractline.itemsetid, contractline.langcode, contractline.leadtime, contractline.leaseendvalue, contractline.linecost, contractline.linecost2, contractline.linestatus, contractline.linetype, contractline.manufacturer, contractline.modelnum, contractline.orderqty, contractline.orderunit, contractline.orgid, contractline.porequired, contractline.remark, contractline.revisionnum, contractline.revstatus, contractline.rowstamp, contractline.unitcost from contractline where contracttype in (select value from synonymdomain where domainid ='CONTRACTTYPE' and maxvalue in ('LEASE', 'RENTAL'));
