-- VIEW: MAXIMO.SCHPURCHVIEW
CREATE OR REPLACE VIEW "MAXIMO"."SCHPURCHVIEW" ("CONTRACTLINEID", "CONTRACTLINENUM", "CONTRACTNUM", "DESCRIPTION", "ENDDATE", "GLCREDITACCT", "GLDEBITACCT", "INCLUSIVE1", "INCLUSIVE2", "INCLUSIVE3", "INCLUSIVE4", "INCLUSIVE5", "INTERIMCHARGE", "LEASERATEFACTOR", "LINECOST", "LOADEDCOST", "ORGID", "PERIODICPAYMENT", "REVISIONNUM", "ROWSTAMP", "SCHEDULE", "SCHEDULEID", "SCHEDULENUM", "SCHEDULEREV", "SCHEDULETYPE", "SINGLELINE", "STARTDATE", "TARGINVSTATUS", "TAX1", "TAX1CODE", "TAX2", "TAX2CODE", "TAX3", "TAX3CODE", "TAX4", "TAX4CODE", "TAX5", "TAX5CODE") AS SELECT schedule.contractlineid, schedule.contractlinenum, schedule.contractnum, schedule.description, schedule.enddate, schedule.glcreditacct, schedule.gldebitacct, schedule.inclusive1, schedule.inclusive2, schedule.inclusive3, schedule.inclusive4, schedule.inclusive5, schedule.interimcharge, schedule.leaseratefactor, schedule.linecost, schedule.loadedcost, schedule.orgid, schedule.periodicpayment, schedule.revisionnum, schedule.rowstamp, schedule.schedule, schedule.scheduleid, schedule.schedulenum, schedule.schedulerev, schedule.scheduletype, schedule.singleline, schedule.startdate, schedule.targinvstatus, schedule.tax1, schedule.tax1code, schedule.tax2, schedule.tax2code, schedule.tax3, schedule.tax3code, schedule.tax4, schedule.tax4code, schedule.tax5, schedule.tax5code from schedule where scheduletype in (select value from synonymdomain where domainid ='SCHEDULETYPE' and maxvalue = 'PURCH');
