-- VIEW: MAXIMO.SKDEXTRACAPCRAFTVIEW
CREATE OR REPLACE VIEW "MAXIMO"."SKDEXTRACAPCRAFTVIEW" ("CRAFT", "CRAFTSKILLID", "DESCRIPTION", "ENDDATE", "HASLD", "ITEMSETID", "LANGCODE", "ORGID_1", "ORGID_2", "RANK_1", "RANK_2", "RESOURCENAME", "ROWSTAMP_1", "ROWSTAMP_2", "SKDPROJECTID", "SKDWORKPLANEXTCAPID", "SKILLLEVEL_1", "SKILLLEVEL_2", "SKILLNAME", "STARTDATE", "TYPE", "VALUE") AS SELECT craftskill.craft, craftskill.craftskillid, craftskill.description, skdworkplanextcap.enddate, craftskill.hasld, skdworkplanextcap.itemsetid, craftskill.langcode, skdworkplanextcap.orgid AS "ORGID_1", craftskill.orgid AS "ORGID_2", craftskill.rank AS "RANK_1", skdworkplanextcap.rank AS "RANK_2", skdworkplanextcap.resourcename, craftskill.rowstamp AS "ROWSTAMP_1", skdworkplanextcap.rowstamp AS "ROWSTAMP_2", skdworkplanextcap.skdprojectid, skdworkplanextcap.skdworkplanextcapid, skdworkplanextcap.skilllevel AS "SKILLLEVEL_1", craftskill.skilllevel AS "SKILLLEVEL_2", skdworkplanextcap.skillname, skdworkplanextcap.startdate, skdworkplanextcap.type, skdworkplanextcap.value from craftskill, skdworkplanextcap where skdworkplanextcap.resourcename=craftskill.craft and skdworkplanextcap.orgid=craftskill.orgid and (skdworkplanextcap.skilllevel=craftskill.skilllevel or (craftskill.skilllevel is null and skdworkplanextcap.skilllevel is null));
