-- VIEW: MAXIMO.TLOAMPROMOTE
CREATE OR REPLACE VIEW "MAXIMO"."TLOAMPROMOTE" ("NODEID", "ASSETCLASS", "NODENAME", "DOMAINNAME", "GUID", "TLOAMHASH", "DESCRIPTION", "DPLDSITEID", "SERIALNUMBER", "TLOAMISPROMOTED", "TLOAMPARENTID") AS SELECT deployedasset.nodeid, deployedasset.assetclass, deployedasset.nodename, deployedasset.domainname, deployedasset.guid, deployedasset.tloamhash, deployedasset.description, deployedasset.siteid as dpldsiteid, deployedasset.serialnumber, deployedasset.tloamispromoted, dpacomputer.tloamparentid from deployedasset left join dpacomputer on deployedasset.nodeid = dpacomputer.nodeid;
