-- VIEW: MAXIMO.TOOLINV
CREATE OR REPLACE VIEW "MAXIMO"."TOOLINV" ("ABCTYPE", "ATTACHONISSUE", "BINNUM", "CAPITALIZED", "CATALOGCODE", "CATEGORY", "CCF", "CLASSSTRUCTUREID", "COMMODITY", "COMMODITYGROUP", "CONDITIONENABLED", "CONSIGNMENT", "CONSVENDOR", "CONTROLACC", "COSTTYPE", "DELIVERYTIME", "DESCRIPTION", "DFLTSTAGEBIN", "EXTERNALREFID_1", "EXTERNALREFID_2", "FREQUENCY", "FREQUNIT", "GLACCOUNT", "GROUPNAME", "HARDRESISSUE_1", "HARDRESISSUE_2", "HASLD", "ICG", "IN19", "IN20", "IN21", "IN22", "IN23", "IN24", "IN25", "IN26", "IN27", "INSPECTIONREQUIRED", "INTERNAL", "INVCOSTADJACC", "INVENTORYID", "INVGENTYPE", "INVMTOL", "INVPTOL", "ISCREW", "ISKIT", "ISSUE1YRAGO", "ISSUE2YRAGO", "ISSUE3YRAGO", "ISSUEUNIT_1", "ISSUEUNIT_2", "ISSUEYTD", "ITEMID", "ITEMNUM_1", "ITEMNUM_2", "ITEMSETID_1", "ITEMSETID_2", "STATUS_1", "STATUSDATE_1", "ITEMTYPE", "LANGCODE", "LASTISSUEDATE", "LOCATION", "LOTTYPE", "MANUFACTURER", "MAXISSUE", "MAXLEVEL", "METERNAME", "MINLEVEL", "MODELNUM", "MSDSNUM", "NEXTINVOICEDATE", "ORDERQTY", "ORDERUNIT_1", "ORDERUNIT_2", "ORGID", "OUTSIDE", "OWNERSYSID_1", "OWNERSYSID_2", "PLUSCISINHOUSECAL", "PLUSCISMTE", "PLUSCISMTECLASS", "PLUSCSOLUTION", "PRORATE", "RECEIPTTOLERANCE_1", "RECEIPTTOLERANCE_2", "REORDER", "ROTATING", "ROWSTAMP_1", "ROWSTAMP_2", "SENDERSYSID_1", "SENDERSYSID_2", "SHRINKAGEACC", "SITEID", "SOURCESYSID_1", "SOURCESYSID_2", "SPAREPARTAUTOADD", "SSTOCK", "STATUS_2", "STATUSDATE_2", "STORELOC", "STORELOCSITEID", "TAXEXEMPT", "VENDOR") AS SELECT inventory.abctype, item.attachonissue, inventory.binnum, item.capitalized, inventory.catalogcode, inventory.category, inventory.ccf, item.classstructureid, item.commodity, item.commoditygroup, item.conditionenabled, inventory.consignment, inventory.consvendor, inventory.controlacc, inventory.costtype, inventory.deliverytime, item.description, inventory.dfltstagebin, inventory.externalrefid AS "EXTERNALREFID_1", item.externalrefid AS "EXTERNALREFID_2", inventory.frequency, inventory.frequnit, inventory.glaccount, item.groupname, item.hardresissue AS "HARDRESISSUE_1", inventory.hardresissue AS "HARDRESISSUE_2", item.hasld, inventory.icg, item.in19, item.in20, item.in21, item.in22, item.in23, item.in24, item.in25, item.in26, item.in27, item.inspectionrequired, inventory.internal, inventory.invcostadjacc, inventory.inventoryid, inventory.invgentype, inventory.invmtol, inventory.invptol, item.iscrew, item.iskit, inventory.issue1yrago, inventory.issue2yrago, inventory.issue3yrago, inventory.issueunit AS "ISSUEUNIT_1", item.issueunit AS "ISSUEUNIT_2", inventory.issueytd, item.itemid, inventory.itemnum AS "ITEMNUM_1", item.itemnum AS "ITEMNUM_2", inventory.itemsetid AS "ITEMSETID_1", item.itemsetid AS "ITEMSETID_2", item.status AS "STATUS_1", item.statusdate AS "STATUSDATE_1", item.itemtype, item.langcode, inventory.lastissuedate, inventory.location, item.lottype, inventory.manufacturer, item.maxissue, inventory.maxlevel, item.metername, inventory.minlevel, inventory.modelnum, item.msdsnum, inventory.nextinvoicedate, inventory.orderqty, item.orderunit AS "ORDERUNIT_1", inventory.orderunit AS "ORDERUNIT_2", inventory.orgid, item.outside, inventory.ownersysid AS "OWNERSYSID_1", item.ownersysid AS "OWNERSYSID_2", item.pluscisinhousecal, item.pluscismte, item.pluscismteclass, item.pluscsolution, item.prorate, inventory.receipttolerance AS "RECEIPTTOLERANCE_1", item.receipttolerance AS "RECEIPTTOLERANCE_2", inventory.reorder, item.rotating, inventory.rowstamp AS "ROWSTAMP_1", item.rowstamp AS "ROWSTAMP_2", inventory.sendersysid AS "SENDERSYSID_1", item.sendersysid AS "SENDERSYSID_2", inventory.shrinkageacc, inventory.siteid, inventory.sourcesysid AS "SOURCESYSID_1", item.sourcesysid AS "SOURCESYSID_2", item.sparepartautoadd, inventory.sstock, inventory.status AS "STATUS_2", inventory.statusdate AS "STATUSDATE_2", inventory.storeloc, inventory.storelocsiteid, item.taxexempt, inventory.vendor from item, inventory where item.itemsetid=inventory.itemsetid and inventory.itemnum=item.itemnum and item.itemtype in (select value from synonymdomain where domainid = 'ITEMTYPE' and maxvalue = 'TOOL');
