-- DDL for VIEW MAXIMO.VW_CRONTASKHIST_LATEST
CREATE OR REPLACE VIEW "MAXIMO"."VW_CRONTASKHIST_LATEST" AS SELECT "CRONTASKNAME","INSTANCENAME","SERVERNAME","SERVERHOST","ACTIVITY","STARTTIME","ENDTIME","RUNTIMEERROR","SEQUENCE","CRONTASKHISTORYID","ROWSTAMP","CORRELATIONID" FROM MAXIMO.CRONTASKHISTORY WHERE STARTTIME >= SYSDATE - 10;
